    private void btnLoginMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnLoginMouseClicked
        String usuario = txtUserEntry.getText();
        String contrasenna = txtPasswEntry.getText();
        
            boolean usuarioEncontrado= false;

            for(Usuarios user: IniciarUsuario.Userlist){
                if(user.getContrasenna().equals(contrasenna) && user.getUsuario().equals(usuario)){

                    JOptionPane.showMessageDialog(this,"Bienvenido");

                    Interface a = new Interface();
                    a.setVisible(true);
                    this.dispose();

                    usuarioEncontrado= true;
                    break;
                }
            }
            if (usuarioEncontrado){
                
                this.dispose();

            }else{
                JOptionPane.showMessageDialog(this,"Usuario o clave incorrecta");

            }

    }//GEN-LAST:event_btnLoginMouseClicked

